import CONST from '@/config/const.js';
import MESSAGES from '@/config/message.js';
import { Prisma } from '@/generated/client.js';
import prisma from '@/utils/prisma.js';
import { zValidator } from '@hono/zod-validator';
import { hash } from 'bcrypt';
import { Hono } from 'hono';
import mjml2html from 'mjml';
import { Resend } from 'resend';
import { z } from 'zod';

const registerSchema = z.object({
    username: z.string().min(CONST.USERNAME_MIN_LENGTH).max(CONST.USERNAME_MAX_LENGTH),
    email: z.email(),
    password: z.string().min(CONST.PASSWORD_MIN_LENGTH).max(CONST.PASSWORD_MAX_LENGTH)
});

const existingUserSelect: Prisma.UserSelect = {
    username: true,
    email: true,
    is_verified: true,
    otp: true
};

const app = new Hono();

app.post('/', zValidator('json', registerSchema), async (c) => {
    try {
        const { username, email, password } = c.req.valid('json');

        const existingUser = await prisma.user.findFirst({
            where: { OR: [{ username }, { email }] },
            select: existingUserSelect
        });

        if (existingUser) {
            return c.json({ success: false, error: `${MESSAGES.userExists.replace('<username>', username).replace('<email>', email)}` }, 400);
        }

        const saltRounds = Number.parseInt(process.env.SALT_ROUNDS || '10');
        const hashedPassword = await hash(password, saltRounds);

        const newOtp = Math.floor(1000 + Math.random() * 9000).toString();

        await prisma.user.create({
            data: {
                username,
                email,
                password: hashedPassword,
                otp: newOtp,
                is_verified: false
            }
        });

        try {
            const config = await prisma.config.findUnique({
                where: { id: 'singleton' }
            });

            if (config?.resend_api) {
                const resend = new Resend(config.resend_api);

                const mjmlTemplate = /* HTML */ `
                    <mjml>
                        <mj-head>
                            <mj-attributes>
                                <mj-all font-family="Montserrat, Arial, sans-serif" />
                                <mj-text line-height="1.5" />
                            </mj-attributes>
                            <mj-preview>Xác thực tài khoản E Roadmap</mj-preview>
                            <mj-style> .otp-code { background: linear-gradient(135deg, #57534e 0%, #44403c 100%); border-radius: 16px; box-shadow: 0 10px 25px rgba(0, 0, 0, 0.1); letter-spacing: 8px; } .welcome-text { background: linear-gradient(135deg, #333333 0%, #57534e 100%); -webkit-background-clip: text; -webkit-text-fill-color: transparent; background-clip: text; } </mj-style>
                        </mj-head>
                        <mj-body background-color="#f8f8f7">
                            <mj-section background-color="#ffffff" padding-bottom="10px" padding-top="30px">
                                <mj-column>
                                    <mj-image width="120px" src="data:image/png;base64," alt="" />
                                </mj-column>
                            </mj-section>

                            <mj-section background-color="#ffffff" padding-bottom="30px" padding-top="10px">
                                <mj-column>
                                    <mj-text font-size="28px" font-weight="bold" align="center" css-class="welcome-text"> Chào mừng ${username}! </mj-text>
                                    <mj-text font-size="16px" color="#57534e" padding="20px 40px 0">
                                        Cảm ơn bạn đã đăng ký tài khoản tại E Roadmap. <br />
                                        Vui lòng sử dụng mã OTP bên dưới để xác thực tài khoản của bạn.
                                    </mj-text>
                                </mj-column>
                            </mj-section>

                            <mj-section background-color="#ffffff" padding-bottom="40px" padding-top="10px">
                                <mj-column>
                                    <mj-raw>
                                        <div style="text-align: center; margin: 25px 0">
                                            <div class="otp-code" style="display: inline-block; padding: 20px 40px; color: #ffffff; font-size: 36px; font-weight: bold">${newOtp}</div>
                                        </div>
                                    </mj-raw>
                                    <mj-text align="center" font-size="14px" color="#78716c">DEMO nên OTP sẽ k hết hạn(đến khi xác nhận)</mj-text>
                                </mj-column>
                            </mj-section>
                        </mj-body>
                    </mjml>
                `;

                const { html } = mjml2html(mjmlTemplate);

                await resend.emails.send({
                    from: `noreply@${process.env.DOMAIN}`,
                    to: [email],
                    subject: `Xác thực tài khoản E Roadmap - ${username}`,
                    html
                });
            }
        } catch {
            // chưa cấu hình domain, api hết hạn, bla bla,(admin active bằng cơm)
        }

        return c.json(
            {
                success: true
            },
            201
        );
    } catch {
        return c.json({ success: false, error: MESSAGES.internalServerError }, 500);
    }
});

export default app;
